// Math3D.htypedef struct _Point3D {	float 		x;	float		y;	float		z;} Point3D;// Last column is always assumed to be 0, 0, 0, 1, we can use this to optimize.typedef struct Matrix4x3 {	float	value[4][3];} Matrix4x3;void SetEmptyMatrix (Matrix4x3 *m);void SetIdentityMatrix (Matrix4x3 *m);void SetTranslationMatrix (Matrix4x3 *m, float dx, float dy, float dz);void MultiplyMatrixToMatrix (const Matrix4x3 *src, const Matrix4x3 *times, Matrix4x3 *dest);void MultiplyMatrixToPoint (Point3D *dest, Matrix4x3 *times);